    id_usuario INT PRIMARY KEY AUTO_INCREMENT,
    tipoUsuario VARCHAR(100) NOT NULL,
    nome VARCHAR(100) NOT NULL,
    username VARCHAR(50) NOT NULL,
    data_nasc VARCHAR(50) NOT NULL,
